import React, { Component } from "react";
import { PieChart } from 'react-minimal-pie-chart';
    

export default class Usage extends Component {


    constructor(props) {
        super(props);
      }

    componentDidMount() {

        const requestOptions = {
            method: 'GET',
            headers: { 'Access-Control-Request-Method': 'GET'
            , 'Access-Control-Request-Headers': '*'}
        };

        fetch('https://86wu00bura.execute-api.us-east-1.amazonaws.com/v1/authenticate', requestOptions)
        .then(response => {
            

            // check for error response
            if (!response.ok) {
                // get error message from body or default to response statusText
                const error = "There was some problem in the request. Please try again."
                return Promise.reject(error);
            }
            return response.json()
        })
        .then(data => {

            console.log(data)
            if (data.statusCode != 200) {
                // get error message from body or default to response statusText
                const error = data.body.json().message
                return Promise.reject(error);
            }
            const uid = (JSON.parse(data.body)).id
            this.setState({isLoggedIn: true, userName: this.state.userName, password: this.state.password, erroruserName: this.state.erroruserName, errorPassword: this.state.errorPassword, userid: uid})
            

        })
        .catch(error => {
            console.error('There was an error!', error);
            alert('Invalid credentials ! Please try again.')
        });

    }


    render(){
        console.log("Helloooooooooooo")
        console.log(this.props.userid.userid.location.state.userid)
        return(
            <div>
                <div className="row">
                    
                    <div className="col-md-12">
                        <div className="row">
                        <span>Subscription type: </span>&nbsp;&nbsp;&nbsp;Basic
                        </div>
                        &nbsp;
                        <div className="row">
                        <span>Subscription Usage: </span><span>
                            
                            <PieChart
                                data={[
                                    { title: '100%', value: 10, percentage: 100, color: '#006600' }
                                ]}  animate
                                animationDuration={500}
                                animationEasing="ease-out" label={(data) => data.dataEntry.title} labelStyle={{
                                    fontSize: "8px",
                                    fontColor: "FFFFFA",
                                    fontWeight: "800",
                                  }} lineWidth={100} reveal={100} radius={35}
                            /></span>
                        </div>
                    </div>
                    
                </div>
            </div>
        )  
    }
}
