import React, { Component } from "react";

export default class SignUp extends Component {

    constructor(props){
        super(props);
        this.state = {firstName:'', lastName:'', email:'', password:'', errors:'', errorEmail:'', errorfirstName:'', errorlastName:'', errorPassword:''}
      }


    onSubmit(e) {
        e.preventDefault();
        if(this.state.error!='') {
            alert("Please enter valid field values before submission")
        }
        else
            alert("All good !!")
     }

    handleEmailChange(e) {
        const em = new RegExp("^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$")
        console.log("On email change")
        this.setState({email: e.target.value});
        if(e.target.value=='')
            this.setState({errors: 'Required', errorEmail:'email'});
        else if(e.target.value.length>0 && !em.test(e.target.value))
            this.setState({errors: 'Invalid email address', errorEmail:'email'});
        else 
            this.setState({errors: '', errorEmail:'', errorfirstName:'', errorlastName:'', errorPassword:''});
     }

     handlefirstNameChange(e) {
        const fName = new RegExp("[a-zA-Z]")
        this.setState({firstName: e.target.value});
        if (e.target.value.length>15)
            this.setState({errors: 'Too long', errorfirstName:'firstName'});
        else if(e.target.value=='')
            this.setState({errors: 'Required', errorfirstName:'firstName'});
        else if(e.target.value.length>0 && !fName.test(e.target.value))
            this.setState({errors: 'Must contain characters only', errorfirstName:'firstName'});
        else 
            this.setState({errors: '', errorEmail:'', errorfirstName:'', errorlastName:'', errorPassword:''});

     }

     handlelastNameChange(e) {
        const lName = new RegExp("[a-zA-Z]")
        console.log("On last name change")
        this.setState({lastName: e.target.value});
        if(e.target.value=='')
            this.setState({errors: 'Required', errorlastName:'lastName'});
        else if (e.target.value.length>15)
            this.setState({errors: 'Too long', errorlastName:'lastName'});
        else if(e.target.value.length>0 && !lName.test(e.target.value))
            this.setState({errors: 'Must contain characters only', errorlastName:'lastName'});
        else 
            this.setState({errors: '', errorEmail:'', errorfirstName:'', errorlastName:'', errorPassword:''});
     }

     handlePasswordChange(e) {
        const pwd = new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})")
        this.setState({password: e.target.value});
        if(e.target.value=='')
            this.setState({errors: 'Required', errorPassword:'password'});
        else if (e.target.value.length>15)
            this.setState({errors: 'Too long', errorPassword:'password'});
        else if(e.target.value.length>0 && !pwd.test(e.target.value))
            this.setState({errors: 'Min 8 characters, atleast one uppsercase, atleats one lower case, one number and one special character', errorfield:'password'});
        else 
            this.setState({errors: '', errorEmail:'', errorfirstName:'', errorlastName:'', errorPassword:''});
     }

    render() {
        return (
            
            <div class="row" style={{paddingTop:100}}>
                <div class="col-md-4"></div>
                <div class="col-md-4">
            <form>
                <h3 >Create Account</h3>
                
                

                <div className="form-group" >
                    
                    <input type="text" className="form-control" required placeholder="Enter First name*" value={this.state.firstName} onChange={this.handlefirstNameChange.bind(this)}/>
                    {this.state.errors && (this.state.errorfirstName=="firstName") && <div  style={{float:'left'}}><p style={{color: 'red'}}>{this.state.errors}</p></div>}
                </div>

                <div className="form-group">
                    
                    <input type="text" className="form-control" required placeholder="Enter Last name*" value={this.state.lastName} onChange={this.handlelastNameChange.bind(this)}/>
                    {this.state.errors && (this.state.errorlastName=="lastName") && <div  style={{float:'left'}}><p style={{color: 'red'}}>{this.state.errors}</p></div>}
                </div>

                <div className="form-group">
                    <input type="email" className="form-control" required placeholder="Enter email*" value={this.state.email} onChange={this.handleEmailChange.bind(this)}/>
                    {this.state.errors && (this.state.errorEmail=="email") && <div  style={{float:'left'}}><p style={{color: 'red'}}>{this.state.errors}</p></div>}
                </div>

                <div className="form-group">
                    <input type="password" className="form-control" required placeholder="Enter password*" value={this.state.password} onChange={this.handlePasswordChange.bind(this)}/>
                    {this.state.errors && (this.state.errorPassword=="password" || this.state.errorfield=='req') && <div  style={{float:'left'}}><p style={{color: 'red'}}>{this.state.errors}</p></div>}
                </div>

                <button type="submit" className="btn btn-primary btn-block" onClick={this.onSubmit.bind(this)}>Sign Up</button>
                <p className="forgot-password text-right">
                    Already registered ? <a href="/">Login here</a>
                </p>
            </form>
            </div><div class="col-md-4"></div>
            </div>
        );
    }
}
