import React, { Component } from "react";
import { Navbar,Nav} from 'react-bootstrap'



import Child from "./child.component";

export default class Main extends Component {

   

    constructor(props) {
        super(props);
        this.handleUpload = this.handleUpload.bind(this);
        this.handleUsage = this.handleUsage.bind(this);
        this.state = {isUpload: 1, uname:''};
      }

    componentDidMount() {

        console.log(this.props)

        const requestOptions = {
            method: 'GET',
            headers: { 'Access-Control-Request-Method': 'GET'
            , 'Access-Control-Request-Headers': '*', 'x-auth-token': 'auth'}
            
        };

        let fetch_url = 'https://86wu00bura.execute-api.us-east-1.amazonaws.com/v1/user'+this.props.location.state.userid

        fetch(fetch_url, requestOptions)
        .then(response => {
            

            // check for error response
            if (!response.ok) {
                // get error message from body or default to response statusText
                const error = "There was some problem in the request. Please try again."
                return Promise.reject(error);
            }
            return response.json()
        })
        .then(data => {

            console.log(data)
            if (data.statusCode != 200) {
                // get error message from body or default to response statusText
                const error = (JSON.parse(data.body)).message
                return Promise.reject(error);
            }
            const uid = (JSON.parse(data.body)).id
            //this.setState({isUpload:,username: data.name})
            

        })
        .catch(error => {
            console.error('There was an error!', error);
            alert(error)
        });
        

    }
    
      handleUpload() {
        this.setState({isUpload: 1});
        //alert(this.state.isUpload)
      }
    
      handleUsage() {
        this.setState({isUpload: 0});
        //alert(this.state.isUpload)
      }

    render(){

        
        return(

            

            <div>
                <div className="row">
                    <div className="col-md-12">
                            <Navbar bg="dark" variant="dark" expand="lg" sticky="top">
                                <Navbar.Brand href="#home">Caption It !</Navbar.Brand>
                                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                                <Navbar.Collapse id="basic-navbar-nav">
                                    <Nav className="mr-auto">
                                    <Nav.Link onClick={this.handleUpload}>Upload an image</Nav.Link>
                                    
                                    <Nav.Link onClick={this.handleUsage}>My usage</Nav.Link>
                                    
                                    
                                    </Nav>
                                    <Nav>
                                    <Nav class="navbar-brand pull-right">Hi Sudha !</Nav></Nav>
                                    <Nav>
                                    <Nav.Link class="navbar-brand pull-right" href="/logout">Logout</Nav.Link></Nav>
                                </Navbar.Collapse>
                            </Navbar>
                        
                    
                <Child data = {this.state.isUpload} userid= {this.props}></Child>
                </div>
                    </div>
                
            </div>
        )  
    }
}
