import React, { Component } from "react";

export default class SignUp extends Component {

    constructor(props){
        super(props);
        this.state = {firstName:'', lastName:'', email:'', password:'', errors:'', errorfield:''}
      }


    onSubmit(e) {
        console.log(e)
        console.log("On submit")
     }

    handleEmailChange(e) {
        const em = new RegExp("\b[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}\b.")
        console.log("On email change")
        this.setState({email: e.target.value});
        if (e.target.value.length>15)
            this.setState({errors: 'Too long', errorfield:'email'});
        else if(e.target.value.length>0 && !em.test(e.target.value))
            this.setState({errors: 'Invalid email address', errorfield:'email'});
        else 
            this.setState({errors: '', errorfield:''});
     }

     handlefirstNameChange(e) {
        const fName = new RegExp("[a-zA-Z]")
        this.setState({firstName: e.target.value});
        if (e.target.value.length>15)
            this.setState({errors: 'Too long', errorfield:'firstName'});
        else if(e.target.value.length>0 && !fName.test(e.target.value))
            this.setState({errors: 'Must contain characters only', errorfield:'firstName'});
        else 
            this.setState({errors: '', errorfield:''});

     }

     handlelastNameChange(e) {
        const lName = new RegExp("[a-zA-Z]")
        console.log("On last name change")
        this.setState({lastName: e.target.value});
        if (e.target.value.length>15)
            this.setState({errors: 'Too long', errorfield:'lastName'});
        else if(e.target.value.length>0 && !lName.test(e.target.value))
            this.setState({errors: 'Must contain characters only', errorfield:'lastName'});
        else 
            this.setState({errors: '', errorfield:''});
     }

     handlePasswordChange(e) {
        const pwd = new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$")
        this.setState({email: e.target.value});
        if (e.target.value.length>15)
            this.setState({errors: 'Too long', errorfield:'password'});
        else if(e.target.value.length>0 && !pwd.test(e.target.value))
            this.setState({errors: 'Min 8 characters, atleast one uppsercase, atleats one lower case, one number and one special character', errorfield:'password'});
        else 
            this.setState({errors: '', errorfield:''});
     }

    render() {
        return (
            
            <div class="row" style={{paddingTop:100}}>
                <div class="col-md-4"></div>
                <div class="col-md-4">
            <form>
                <h3 >Register</h3>
                
                

                <div className="form-group" >
                    
                    <input type="text" className="form-control" required placeholder="First name" value={this.state.firstName} onChange={this.handlefirstNameChange.bind(this)}/>
                    {this.state.errors && this.state.errorfield=="firstName" && <div  style={{float:'left'}}><p style={{color: 'red'}}>{this.state.errors}</p></div>}
                </div>

                <div className="form-group">
                    
                    <input type="text" className="form-control" required placeholder="Last name" value={this.state.lastName} onChange={this.handlelastNameChange.bind(this)}/>
                    {this.state.errors && this.state.errorfield=="lastName" && <div  style={{float:'left'}}><p style={{color: 'red'}}>{this.state.errors}</p></div>}
                </div>

                <div className="form-group">
                    <input type="email" className="form-control" required placeholder="Enter email" value={this.state.email} onChange={this.handleEmailChange.bind(this)}/>
                    {this.state.errors && this.state.errorfield=="email" && <div  style={{float:'left'}}><p style={{color: 'red'}}>{this.state.errors}</p></div>}
                </div>

                <div className="form-group">
                    <input type="password" className="form-control" required placeholder="Enter password" value={this.state.password} onChange={this.handlePasswordChange.bind(this)}/>
                    {this.state.errors && this.state.errorfield=="password" && <div  style={{float:'left'}}><p style={{color: 'red'}}>{this.state.errors}</p></div>}
                </div>

                <button type="submit" className="btn btn-primary btn-block" onClick={this.onSubmit.bind(this)}>Sign Up</button>
                <p className="forgot-password text-right">
                    Already registered ? <a href="/">Login here</a>
                </p>
            </form>
            </div><div class="col-md-4"></div>
            </div>
        );
    }
}
