import React, { Component } from "react";
import Result from "./result.component";
import ImageUploader from 'react-images-upload';



export default class Upload extends Component {

    constructor(props) {
        super(props);
        this.state = {file: null, url: null, error:false, showResults:false, base64str:''};
      }


    

    urlChanged(event){

        this.setState({file: this.state.file, url:event.target.value, error:this.state.error, showResults:this.state.showResults, base64str:this.state.base64str})
        
    }

    fileChangedHandler(picture){

        this.setState({file: picture, url:this.state.url, error:this.state.error, showResults:this.state.showResults, base64str:this.state.base64str})
      
    }

    getBase64(file, cb) {
        let reader = new FileReader();
        reader.readAsDataURL(file);
    
        reader.onload = function () {
            cb(reader.result)
            
        }
        reader.onerror = function (error) {
            console.log('Error: ', error);
        }


    }
      
    uploadHandler(){

        if ((this.state.url == null || this.state.url == '') && (this.state.file == null || this.state.file == '')) {
            this.setState({file: this.state.file, url:this.state.url, error:true, showResults:this.state.showResults})
        }
        else {

            console.log(this.state.file)

            const uid = this.props.userid.userid.location.state.userid

            
            let imgBase64 = '';
            this.getBase64(this.state.file[0], (result) => {
            console.log(result)
            imgBase64 = result
            })

            console.log(imgBase64)
            //let reader = new FileReader();
            //let b6 = reader.readAsDataURL(this.state.file);

            //console.log(b6)

            

            
            const requestOptions = {
                method: 'POST',
                headers: { 'Access-Control-Request-Method': 'POST'
                , 'Access-Control-Request-Headers': '*'},
                body: JSON.stringify({ 
                    'id': uid,
                    'image': !this.state.file ? this.state.url : this.state.file
                  })
            };
    
            fetch('https://86wu00bura.execute-api.us-east-1.amazonaws.com/v1/caption', requestOptions)
            .then(response => {
                
    
                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response statusText
                    const error = "There was some problem in the request. Please try again."
                    return Promise.reject(error);
                }
                return response.json()
            })
            .then(data => {
    
                console.log(data)
                if (data.statusCode != 200) {
                    // get error message from body or default to response statusText
                    const error = (JSON.parse(data.body)).message
                    return Promise.reject(error);
                }
                const uid = (JSON.parse(data.body)).id
                this.setState({isLoggedIn: true, userName: this.state.userName, password: this.state.password, erroruserName: this.state.erroruserName, errorPassword: this.state.errorPassword, userid: uid})
                
    
            })
            .catch(error => {
                console.error('There was an error!', error);
                alert(error)
            });

            this.setState({file: this.state.file, url:this.state.url, error:false, showResults:true})
            
        }
    }

    reset() {
        document.getElementById("imgfile");
    }

    render(){
        return(
            <div>
                <div className="row">
                    <div className="col-md-2"></div>
                    <div className="col-md-8">
                    <form id="create-course-form">
                        <br/>
                        <div class="form-group">
                            <input type="url" class="form-control"  placeholder="Enter URL" onChange={this.urlChanged.bind(this)}/>
                            
                            
                        </div>
                        <div>
                        <label><br/>OR</label></div>
                        <br/>
                        <div class="form-group">
                            
                        <ImageUploader id="imgfile"
                withIcon={true}
                buttonText='Browse image'
                onChange={this.fileChangedHandler.bind(this)}
                imgExtension={['.jpg']}
                maxFileSize={5242880}
                class="form-control"  placeholder="Image"
            />
                            {this.state.error && <div  style={{float:'left'}}><p style={{color: 'red'}}>Should provide URL or upload an image</p></div>}
                        </div>
                        <br/>
                        <br/>
                        
                        <button type="button" class="btn btn-primary" onClick={this.uploadHandler.bind(this)}>Get caption</button>
                        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        <button type="submit" class="btn btn-primary" onClick={this.reset.bind(this)}>Reset</button>
                    </form>
                    </div>
                    {this.state.showResults && <div className="col-md-2"><Result file={this.state.file}></Result></div>}
                    
                    
                </div>
            </div>
        )  
    }
}
