import React, { Component } from "react";
import Result from "./result.component";


export default class Upload extends Component {

    constructor(props) {
        super(props);
        this.state = {file: null, url: null};
      }

    urlChanged = (event) => {

        this.setState({file: this.state.file, url:event.target.value})
        
    }

    fileChangedHandler = (event) => {

        this.setState({file: event.target.files[0], url:this.state.url})
      
    }
      
    uploadHandler = () => {

        if (this.state.url == '' && this.state.file == '') {
            alert("Should provide URL or upload an image")
        }
    }

    render(){
        return(
            <div>
                <div className="row">
                    <div className="col-md-2"></div>
                    <div className="col-md-8">
                    <form>
                        <br/>
                        <div class="form-group">
                            <input type="url" class="form-control"  placeholder="Enter URL" onChange={this.urlChanged.bind(this)}/>
                            
                            
                        </div>
                        <div>
                        <label><br/>OR</label></div>
                        <br/>
                        <div class="form-group">
                            
                            <input type="file" onChange={this.fileChangedHandler.bind(this)} class="form-control"  placeholder="Image"/>
                        </div>
                        <br/>
                        <br/>
                        
                        <button type="submit" class="btn btn-primary" onClick={this.uploadHandler.bind(this)}>Get caption</button>
                        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        <button type="submit" class="btn btn-primary">Reset</button>
                    </form>
                        
                    </div>
                    <div className="col-md-2"><Result data = {this.state.file}/></div>
                </div>
            </div>
        )  
    }
}
